import{r as o,o as s,c as r,a as e,d as n,F as c,e as i,b as t}from"./app.88d1a4f1.js";import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";var d="/assets/signup-new.dcf672b4.png",h="/assets/lets-get-started-new.a0c0bc34.png",u="/assets/onboarding-completed.90091aac.png",p="/assets/windowsext.3d9464d5.png",g="/assets/winenvvar1.4cc3e825.png",f="/assets/winenvvar2.d9cc5576.png",m="/assets/cmdline.949accea.png",y="/assets/lnx-vcn-1.a442958c.png",v="data:image/png;base64,",b="/assets/vcn-login.45c256a7.png",w="/assets/notarize.6e76d939.png",_="/assets/vcn-dir.a1b16941.png",A="/assets/readonly.b28c552c.png",C="/assets/db-alerts.6f7b63f4.png",k="/assets/alerttriggered.7a1df5c9.png",x="/assets/email.7db2c101.png",T="/assets/refcard.8e51fc04.png";const z={},I=i('<h1 id="quickstart-help-guide" tabindex="-1"><a class="header-anchor" href="#quickstart-help-guide" aria-hidden="true">#</a> Quickstart Help Guide</h1><ul><li><a href="#quickstart-help-guide">Quickstart Help Guide</a></li><li><a href="#open-source">Open Source</a></li><li><a href="#registration">Registration</a></li><li><a href="#installing-the-codenotary-tools">Installing the Codenotary tools</a></li><li><a href="#install-vcn-for-windows">Install VCN for Windows</a></li><li><a href="#integrating-vcn-in-the-windows-path-variable">Integrating vcn in the windows PATH variable</a></li><li><a href="#download-integrity-check">Download integrity check</a></li><li><a href="#install-vcn-cli-for-linux">Install vcn CLI for Linux</a></li><li><a href="#running-vcn">Running vcn</a></li><li><a href="#sanity-checks">Sanity checks</a></li><li><a href="#vcn-authenticate-verify-vcn-itself">vcn authenticate (verify) vcn itself</a></li><li><a href="#vcn-login">vcn login</a></li><li><a href="#vcn-assets">vcn assets</a></li><li><a href="#vcn-authenticate-any-asset">vcn authenticate (any asset)</a></li><li><a href="#vcn-notarizetrustuntrust-any-asset">vcn notarize/trust/untrust (any asset)</a></li><li><a href="#vcn-list">vcn list</a></li><li><a href="#vcn-output-format">vcn output format</a></li><li><a href="#create-alerts-on-files-or-directories-integrity-check">Create alerts on files&#39; or directories&#39; integrity check</a></li><li><a href="#how-to-create-an-alert-with-codenotary">How to create an alert with Codenotary</a></li><li><a href="#activate-the-filedirectory-integrity-monitoring">Activate the file/directory integrity monitoring</a></li><li><a href="#add-the-authentication-line-to-etccrontab-for-periodic-autnetication-sudo-nano-etccrontab">Add the authentication line to /etc/crontab for periodic authentication, sudo nano /etc/crontab:</a></li><li><a href="#access-to-source-opening-issues-and-proposals-for-enhancements">Access to source, opening issues and proposals for enhancements</a></li><li><a href="#cheat-sheet">Cheat sheet</a></li><li><a href="#resources">Resources</a></li></ul><p>Codenotary has the ambitious goal to become the standard for modern-age code notarization. A truly global, decentralized solution to enable secure source to production in your DevOps process in the simplest possible way.</p><p>This page explains how you can use the Codenotary dashboard and vcn command line tool (CLI) to notarize code, documents, and files (globally known as assets) using your Codenotary account.</p><p>When code is notarized, a unique hash is created for the digital asset and notarized with your unique identity. This guarantees that the file integrity is stored in a tamper-proof way. File integrity doesn\u2019t judge the file content. What it does do however is ensure the asset has not been modified since it was notarized.</p><p>This quick help guide gives a brief explanation and ways to use the Codenotary CLI (vcn), and how to notarize (sign) and authenticate (verify)</p><h2 id="open-source" tabindex="-1"><a class="header-anchor" href="#open-source" aria-hidden="true">#</a> Open Source</h2><p>Codenotary is Open Source and the vcn command is published under GPL3 as Open Source, so that it can be reviewed for security and enhanced by the community as well as built by anyone. All code is written in Golang.</p><h2 id="registration" tabindex="-1"><a class="header-anchor" href="#registration" aria-hidden="true">#</a> Registration</h2>',9),q=t("Registering a Codenotary account is performed by visiting the "),P={href:"https://dashboard.codenotary.io",target:"_blank",rel:"noopener noreferrer"},D=t("Codenotary dashboard"),G=t(" web site."),L=e("p",null,[e("img",{src:d,alt:"signup"})],-1),S=e("p",null,"Assuming the email entered is not an existing account, clicking the register button registers the email as a new account.",-1),F=e("p",null,"The next page includes a brief presentation and a link to allow the confirmation email to be resent. The confirmation email contains a link that allows the email address to be confirmed and to continue the process.",-1),W=e("p",null,[e("img",{src:h,alt:"onboard1"})],-1),N=e("p",null,"The account profile information is provided here (and can be changed afterwards). The NEXT button takes you to the dashboard homepage below.",-1),O=e("p",null,[e("img",{src:u,alt:"get started"})],-1),R=t("The dashboard homepage summarizes all key capabilities of Codenotary. From here you can download the command line (CLI) utility vcn for the various supported operating systems so that you can notarize and authenticate digital objects from command line or from scripts. The CLI can also be accessed later from the dashboard toolbar and also at "),E={href:"https://github.com/vchain-us/vcn/releases",target:"_blank",rel:"noopener noreferrer"},V=t("Github"),B=t('. On this page, you have also the list of all available integrations for Codenotary. Now, you can notarize files and digital objects directly from the homepage with a simple drag&drop of the file(s) in the "Notarize Files" square.'),K=i('<h2 id="installing-the-codenotary-tools" tabindex="-1"><a class="header-anchor" href="#installing-the-codenotary-tools" aria-hidden="true">#</a> Installing the Codenotary tools</h2><h3 id="install-vcn-for-windows" tabindex="-1"><a class="header-anchor" href="#install-vcn-for-windows" aria-hidden="true">#</a> Install VCN for Windows</h3><p>Installing vcn for windows requires a 64 bit windows 10 or better system. The vcn for windows tools have 2 versions. The first is a setup version that provides Windows file explorer integration.</p><p><img src="'+p+'" alt="winext"></p><p>There is also a standalone command line version. The setup version optionally installs the vcn command line interface, and this is recommended. The vcn command line executable is identical between the setup version and the standalone version for any given release, so installing both versions is not required.</p><p>Besides the fact that the windows executable are signed using CA (Certificate Authority) based signatures for easier Windows integration, they are also notarized using the Codenotary product which allows, at any time, for the product development team to indicate that a specific binary or package is no longer trusted or supported - something the CA based signing system does not permit.</p><h2 id="integrating-vcn-in-the-windows-path-variable" tabindex="-1"><a class="header-anchor" href="#integrating-vcn-in-the-windows-path-variable" aria-hidden="true">#</a> Integrating vcn in the Windows PATH variable</h2><p>This is an optional step, but allows easier access to vcn from the command line. In Windows Explorer, right click on the icon that represents the machine and select</p><ul><li>Properties</li><li>Click Advanced settings</li><li>Click the environment variables</li></ul><p><img src="'+g+'" alt="winenvvar1"></p><ul><li>Double click on the environment variable Path for the current user</li><li>Add an entry named %PROGRAMFILES%\\Codenotary and click OK</li></ul><p><img src="'+f+'" alt="winenvvar2"></p><p>The system PATH variable can also be changed in which case the vcn command will be available to all users on that system.</p><p>Subsequently opening a new Command (cmd) Window or Powershell window, typing the \u201Cvcn\u201D command should yield a help message (this does not have or should not be an administrative shell).</p><p><img src="'+m+'" alt="cmdline"></p><h2 id="download-integrity-check" tabindex="-1"><a class="header-anchor" href="#download-integrity-check" aria-hidden="true">#</a> Download integrity check</h2>',16),X=t("We advise to verifying the integrity of the vcn CLI download by dragging and dropping the file on "),J={href:"https://authenticate.codenotary.io/",target:"_blank",rel:"noopener noreferrer"},M=t("https://authenticate.codenotary.io/i"),Z=t("."),H=e("p",null,"The vcn Install for Windows has been signed by Digital Certificate release to vChain, Inc., the company behind Codenotary. As we know, the digital certificate only certifies that the download was not altered, but provides no information on whether it is still trusted or if it has become obsolete, something that the Codenotary product can do.",-1),Q=e("p",null,"When using Linux, we advise to verify the checksum of the downloaded file with the following command:",-1),j=e("pre",null,[e("code",null,`sha256sum vcn-<version downloaded>-linux-amd64
`)],-1),U=t("The resulting checksum should be identical to the one found in the description at "),Y={href:"https://github.com/vchain-us/vcn/releases",target:"_blank",rel:"noopener noreferrer"},$=t("Github"),ee=t(". Similar to the digital certificate for Windows, the checksum only ensures the file\u2019s contents integrity, not it\u2019s support status which is ensured using Codenotary itself."),te=i(`<h2 id="install-vcn-cli-for-linux" tabindex="-1"><a class="header-anchor" href="#install-vcn-cli-for-linux" aria-hidden="true">#</a> Install vcn CLI for Linux</h2><p>As for the Windows environment, once the vcn command line binary has been downloaded/installed it should be made available for general users. The simplest way to do this under a Linux operating system is to copy it and make it available under a well-known path (for example : <code>/usr/local/bin</code>).</p><p>As root (logging in as root, using <code>su</code> or <code>sudo</code> or whatever is appropriate for the target environment) copy the downloaded file to /usr/local/bin/vcn and make it executable with :</p><pre><code>cp vcn-&lt;version downloaded&gt;-linux-amd64 /usr/local/bin/vcn
chmod +x /usr/local/bin/vcn
</code></pre><h2 id="running-vcn" tabindex="-1"><a class="header-anchor" href="#running-vcn" aria-hidden="true">#</a> Running vcn</h2><h3 id="sanity-checks" tabindex="-1"><a class="header-anchor" href="#sanity-checks" aria-hidden="true">#</a> Sanity checks</h3><p>To ensure vcn is correctly installed, typing \u201Cvcn\u201D should display :</p><p><img src="`+y+'" alt="img-lnx-1"></p><p>Or something similar. The actual output may depend on the current version of the Codenotary CLI (vcn).</p><h3 id="vcn-authenticate-verify-vcn-itself" tabindex="-1"><a class="header-anchor" href="#vcn-authenticate-verify-vcn-itself" aria-hidden="true">#</a> vcn authenticate (verify) vcn itself</h3><p>The first real sanity check is to ensure \u201Cvcn\u201D is itself notarized.</p><p>This can be done by using Windows explorer (if on Windows and the explorer integration was installed) using the contextual menu or by using the command line interface directly.</p><p>The output should contain this line, or something similar as the date might be different.</p><p><img src="'+v+'" alt="vcn-self-trust"></p><p>As a reminder, authenticating (verifying) assets never requires to be logged in to Codenotary.</p><p>If the output doesn\u2019t indicate &quot;TRUSTED&quot; then it is possible this binary was deemed unsupported or untrusted by the development team between the time it was downloaded and the time it was authenticated with Codenotary.</p><h2 id="vcn-login" tabindex="-1"><a class="header-anchor" href="#vcn-login" aria-hidden="true">#</a> vcn login</h2><p>The next step is to establish a relationship between the Codenotary account created earlier and the machine that is running vcn.</p><p>This is accomplished using the \u201Cvcn login\u201D command and will enable notarization (signing) of assets.</p><p><img src="'+b+'" alt="vcnlogin"></p><p>The login password is the one used during registration.</p><h2 id="vcn-assets" tabindex="-1"><a class="header-anchor" href="#vcn-assets" aria-hidden="true">#</a> vcn assets</h2><p>Codenotary vcn can authenticate/notarize/inspect a wide range (and expanding) type of assets. This includes :</p><ul><li>A file by simply specifying the file name or <code>file://&lt;file&gt;</code></li><li>A docker image by specifying <code>docker://&lt;image name or image ID&gt;</code></li><li>A directory with <code>dir://&lt;directory&gt;</code></li><li>A git repository with <code>git://&lt;local repository directory&gt;</code> which addresses the current git commit</li></ul><h3 id="vcn-authenticate-any-asset" tabindex="-1"><a class="header-anchor" href="#vcn-authenticate-any-asset" aria-hidden="true">#</a> vcn authenticate (any asset)</h3><p>Authenticating (verifying) an asset is the same as was done previously and can be performed on any kind of supported asset. If the account to which this user is logged in notarized the target asset, this takes precedence.</p><h3 id="vcn-notarize-trust-untrust-any-asset" tabindex="-1"><a class="header-anchor" href="#vcn-notarize-trust-untrust-any-asset" aria-hidden="true">#</a> vcn notarize/trust/untrust (any asset)</h3><p>This is the step where an account holder will indicate how the asset should be viewed to the outside world.</p><p><strong>NOTE : the contents of the asset itself are <em>NEVER</em> uploaded to Codenotary or sent anywhere and rely solely on a locally calculated SHA256 hash to ensure uniqueness of the asset, and it is only the hash that is being notarized/signed.</strong></p><p>By default (when not notarized) an asset will be seen as <em>UNKNOWN</em> because it was never notarized. For example, when authenticating a generated random file this is the expected results.</p><p>When an account holder that is signed in with vcn to Codenotary wants to notarize, untrust or un-support an asset the vcn command is also used :</p><p><img src="'+w+`" alt="notarize"></p><p>This is a private notarization. It is also possible to specify the <code>-p</code> or <code>--public</code> flag to make it public. The difference between public and private notarization is the amount of metadata (file size, file name, dates, etc) that is visible.</p><p>However, whether it is public or private, the \u201Ctrust\u201D status is always visible. The <code>notarize</code> command can be substituted with the <code>untrust</code> (meaning the developer deems this file unsafe) and <code>unsupport</code> (meaning this file may no longer work or may yield unpredictable results).</p><h3 id="vcn-list" tabindex="-1"><a class="header-anchor" href="#vcn-list" aria-hidden="true">#</a> vcn list</h3><p>An additional helpful tool is the possibility to get from the command line the list of assets that have been &quot;notarized&quot; (trusted, untrusted, unsupported) from the account to which vcn is logged in. This is the same information visible on the Codenotary dashboard under the \u201CMy Assets\u201D section.</p><h3 id="vcn-output-format" tabindex="-1"><a class="header-anchor" href="#vcn-output-format" aria-hidden="true">#</a> vcn output format</h3><p>The output of vcn (all commands) can also be formatted in JSON (Javascript Object Notation) or YAML (Yet Another Markup Language) format if needed for example for scripting or automation since the output will be consistent across invocations.</p><h2 id="create-alerts-on-files-or-directories-integrity-check" tabindex="-1"><a class="header-anchor" href="#create-alerts-on-files-or-directories-integrity-check" aria-hidden="true">#</a> Create alerts on files&#39; or directories&#39; integrity check</h2><p>Codenotary allows you to monitor and detect changes in files that may indicate a cyber attack. To set up the file/directory integrity monitoring alert, just use the vcn notarize command and include the alert flag.</p><h3 id="how-to-create-an-alert-with-codenotary" tabindex="-1"><a class="header-anchor" href="#how-to-create-an-alert-with-codenotary" aria-hidden="true">#</a> How to create an alert with Codenotary</h3><p>E.g. notarize netplan configuration file:</p><pre><code>vcn notarize --create-alert --alert-name &quot;netplan config of DESKTOP-82S443U&quot; /etc/netplan/50-cloud-init.yaml
</code></pre><p><img src="`+_+`" alt="alert_dir"></p><p>You might have seen the \u2013no-ignore-file flag. We used that flag to disable the creation of the .vcnignore file inside of the /etc/ssh directory, because our user has no permission to write into that folder. But that also deactivates the change details, like added, modified or deleted file name.</p><p>To make sure the file changes inside the folder (where the user has no write permission) are still tracked, please use the \u2013read-only flag.</p><pre><code>vcn notarize --create-alert --alert-name &quot;ssh config of DESKTOP-82S443U&quot; --read-only dir:///etc/ssh
</code></pre><p><img src="`+A+`" alt="alert-read-only-flag"></p><h3 id="activate-the-file-directory-integrity-monitoring" tabindex="-1"><a class="header-anchor" href="#activate-the-file-directory-integrity-monitoring" aria-hidden="true">#</a> Activate the file/directory integrity monitoring</h3><p>To activate the file integrity monitoring, you can simply run the integrity verification check:</p><pre><code>vcn authenticate --alerts
</code></pre><h3 id="add-the-authentication-line-to-etc-crontab-for-periodic-autnetication-sudo-nano-etc-crontab" tabindex="-1"><a class="header-anchor" href="#add-the-authentication-line-to-etc-crontab-for-periodic-autnetication-sudo-nano-etc-crontab" aria-hidden="true">#</a> Add the authentication line to /etc/crontab for periodic autnetication, sudo nano /etc/crontab:</h3><p>It also makes sense to add that command to your cron job.</p><pre><code># add the following line to /etc/crontab, sudo nano /etc/crontab:
*/5 * * * * dennis /usr/local/bin/vcn authenticate --alerts

# One command, as elivated user:
echo &quot;*/5 * * * * username /usr/local/bin/vcn authenticate --alerts&quot; &gt;&gt; /etc/crontab
</code></pre><p>That cron job runs every 5 minutes the Codenotary authenticate job. As Codenotary has a \u201Cdead man switch\u201D built in, an alert will be triggered as well, if no authenticate command will be received for one hour.</p><h2 id="check-the-existing-file-integrity-alerts" tabindex="-1"><a class="header-anchor" href="#check-the-existing-file-integrity-alerts" aria-hidden="true">#</a> Check the existing file integrity alerts</h2><p>When logging into the Codenotary dashboard, you can find your configured file integrity monitoring alerts in the Alerts section. That way you can see all the alerts of all your systems.</p><p><img src="`+C+'" alt="check-alerts-on-dashboard"></p><h2 id="file-integrity-changed-\u2013-triggering-the-alert" tabindex="-1"><a class="header-anchor" href="#file-integrity-changed-\u2013-triggering-the-alert" aria-hidden="true">#</a> File integrity changed \u2013 triggering the alert</h2><p>As long as no one tampered with your files or folders, you don\u2019t receive any alerts by email or shown within the dashboard. But when the file integrity changes, you immediately get an email alert:</p><p><img src="'+k+'" alt="alerttriggered"></p><p><img src="'+x+'" alt="email-alert"></p><p>For security reasons, we don\u2019t disclose more information, but you can click the dashboard link and login to see all details.</p><p>If you don\u2019t want to receive the alerts anymore, you can either acknowledge it or deactivate the alert completely.</p><p>All alert and file integrity data including history is stored immutable, you need to create a new alert if the file or folder changes are ok and intended and deactivate the \u201Cold\u201D alert.</p><h2 id="access-to-source-opening-issues-and-proposals-for-enhancements" tabindex="-1"><a class="header-anchor" href="#access-to-source-opening-issues-and-proposals-for-enhancements" aria-hidden="true">#</a> Access to source, opening issues and proposals for enhancements</h2>',66),ae=t("The Codenotary Command line interface (CLI) is (c) vChain, Inc. but is open source Free Software under a GPLV3 license. The source code is available at "),ne={href:"https://github.com/vchain-us/vcn",target:"_blank",rel:"noopener noreferrer"},ie=t("Github"),oe=t(". Anyone is welcome to open issues or make pull requests with possible patches which will be reviewed."),se=e("h2",{id:"cheat-sheet",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#cheat-sheet","aria-hidden":"true"},"#"),t(" Cheat sheet")],-1),re=t("The following Cheat Sheet has been compiled to have a quick reference card at hand if needed. The full help is available "),ce={href:"https://docs.codenotary.io",target:"_blank",rel:"noopener noreferrer"},le=t("here"),de=t("."),he=e("p",null,[e("img",{src:T,alt:"refcard"})],-1),ue=e("h2",{id:"resources",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#resources","aria-hidden":"true"},"#"),t(" Resources")],-1),pe={href:"https://dashboard.codenotary.io",target:"_blank",rel:"noopener noreferrer"},ge=t("Codenotary site/dashboard"),fe={href:"https://github.com/vchain-us/vcn",target:"_blank",rel:"noopener noreferrer"},me=t("Github repository"),ye=t(" which also contains work in progress, the github issue/ticket system and more.");function ve(be,we){const a=o("ExternalLinkIcon");return s(),r(c,null,[I,e("p",null,[q,e("a",P,[D,n(a)]),G]),L,S,F,W,N,O,e("p",null,[R,e("a",E,[V,n(a)]),B]),K,e("p",null,[X,e("a",J,[M,n(a)]),Z]),H,Q,j,e("p",null,[U,e("a",Y,[$,n(a)]),ee]),te,e("p",null,[ae,e("a",ne,[ie,n(a)]),oe]),se,e("p",null,[re,e("a",ce,[le,n(a)]),de]),he,ue,e("ul",null,[e("li",null,[e("a",pe,[ge,n(a)])]),e("li",null,[e("a",fe,[me,n(a)]),ye])])],64)}var Ce=l(z,[["render",ve]]);export{Ce as default};
